export default function ContactList({ contacts, onDelete }) {
  if (!contacts || contacts.length === 0) return null;

  return (
    <ul>
      {contacts.map((c) => (
        <li
          key={c._id}
          style={{
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            padding: "10px 0",
            borderBottom: "1px solid #ddd"
          }}
        >
          <span>
            <strong>{c.name}</strong> – {c.phone}
          </span>

          {/* ❌ EDIT REMOVED – ONLY DELETE */}
          <button
            onClick={() => onDelete(c._id)}
            style={{
              background: "red",
              color: "white",
              border: "none",
              padding: "6px 10px",
              borderRadius: "4px",
              cursor: "pointer"
            }}
          >
            Delete
          </button>
        </li>
      ))}
    </ul>
  );
}
